  counter: 0,
  text: '',
  list: []
}

const INCREASE = 'INCREASE';
const DECREASE = 'DECREASE';
const CHANGE_TEXT = 'CHANGE_TEXT';
const ADD_TO_LIST = 'ADD_TO_LIST';

// 액션 생성 함수 
const increase = () => ({
  type: INCREASE,
})

const decrease = () => ({
  type: DECREASE,
})

